mapper.update:
update t_help_doc
set page_view_count = page_view_count + #{pageViewCountIncrease},
    user_view_count = user_view_count + #{userViewCountIncrease}
where help_doc_id = #{helpDocId}

mapper.select:
select count(*)
from t_help_doc_view_record
where help_doc_id = #{helpDocId}
  and user_id = #{userId}

mapper.update:
update t_help_doc_view_record
set page_view_count = page_view_count + 1,
    last_ip         = #{ip},
    last_user_agent = #{userAgent}
where help_doc_id = #{helpDocId}
  and user_id = #{userId}

mapper.select:
select * from t_help_doc_view_record where help_doc_id = #{queryForm.helpDocId}
AND ( INSTR(user_name,#{queryForm.keywords}) OR INSTR(first_ip,#{queryForm.keywords}) OR INSTR(first_user_agent,#{queryForm.keywords}) OR INSTR(last_ip,#{queryForm.keywords}) OR INSTR(last_user_agent,#{queryForm.keywords}) )
and user_id = #{queryForm.userId}
order by update_time desc,create_time desc
